const minutesEachLoop = 15; // minutes to add
const loopCycle = 1; // seconds delay
let timeObj = context.get("timeObj");

if (timeObj && msg.topic.includes('stop')) {
    clearInterval(timeObj);
    context.set("timeObj", null);
    context.set("orgtopic", null);
    let d = new Date(context.get("date"));
    node.log("STOP    " + d.toLocaleTimeString() + ' ####################################### payload='+msg.payload+' topic='+msg.topic);
    node.log('<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<< ' + d.toISOString());
    node.status({fill:"red",shape:"ring",text:"stopped - " + d.toLocaleTimeString()});
    return null;
} else if (!timeObj && msg.topic.includes('start')) {
    context.set("message", msg);
    context.set("orgtopic", msg.topic);
    let d = new Date();
    let num = Number(msg.payload);
    if (isNaN(num) && num < 24) {
        d.setHours(num);
        d.setMinutes(0);
    } else {
        let dt = new Date(msg.payload);
        if (dt instanceof Date && !isNaN(dt)) {
            d = dt;
        } else {
            d.setHours(0);
            d.setMinutes(0);
        }
    }
    context.set("date", d.getTime());
    msg.tsISO = d.toISOString();
    msg.ts = d.getTime();
    msg.topic += ' ' + d.toLocaleTimeString();
    node.log('>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>');
    node.log("START   " + d.toLocaleTimeString() + ' ####################################### payload='+msg.payload+' topic='+msg.topic);
    node.send(msg);

    let timeObj = setInterval(function(){
        let msg = context.get("message");
        let topic = context.get("orgtopic");
        let d = new Date(context.get("date"));
        //d.setHours(d.getHours()+1);
        let dt = d.getDate();
        let dm = d.getMonth();
        d.setMinutes(d.getMinutes() + minutesEachLoop)
        d.setDate(dt);
        d.getMonth(dm);
        context.set("date", d.getTime());
        msg.tsISO = d.toISOString();
        msg.ts = d.getTime();
        msg.topic = topic + ' ' + d.toLocaleTimeString();
        node.status({fill:"green",shape:"dot",text:"run - " + d.toLocaleTimeString()});
        node.log("sending " + d.toLocaleTimeString() + ' ####################################### payload='+msg.payload+' topic='+msg.topic);
        node.send(msg);
	}, (1000 * loopCycle));
    context.set("timeObj", timeObj);
    node.status({fill:"green",shape:"ring",text:"start - " + d.toLocaleTimeString()});
    return null;
}

