const ACTIVE = 'active';
const CLOSED = 'closed';
const DETECTED = 'detected';
const FOLLOW_SCHEDULE = 'follow schedule';
const GOOD = 'good';
const HEATING = 'heating';
const LOCKED = 'locked';
const MUTED = 'muted';
const OFF = 'off';
const ON = 'on';
const OPEN = 'open';
const PRESENT = 'present';
const PUSHED = 'pushed';
const SLEEPING = 'sleeping';
const TOUCHED = 'touched';
const WET = 'wet';

var v = {
    'value' : null,
    'isBinary' : null
};

if(msg.name == "acceleration") {
    v.value = (msg.value == ACTIVE ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "alarm") {
    v.value = (msg.value != OFF ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "button") {
    v.value = (msg.value != PUSHED ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "carbonMonoxide") {
    v.value = (msg.value == DETECTED ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "consumableStatus") {
    v.value = (msg.value == GOOD ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "contact") {
    v.value = (msg.value == OPEN ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "door") {
    v.value = (msg.value != OPEN ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "lock") {
    v.value = (msg.value == LOCKED ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "lock") {
    v.value = (msg.value == LOCKED ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "motion") {
