}

else if(msg.name == "thermostatSetpointMode") {
    v.value = (msg.value != FOLLOW_SCHEDULE ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "touch") {
    v.value = (msg.value != TOUCHED ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "optimisation") {
    v.value = (msg.value == ACTIVE ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "windowFunction") {
    v.value = (msg.value == ACTIVE ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "water") {
    v.value = (msg.value == WET ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "windowShade") {
    v.value = (msg.value == CLOSED ? 1 : 0);
    v.isBinary = true;
}

else if(msg.name == "threeAxis") {
    var vz = msg.value.split(',');
    var x = vz[0];
    var y = vz[1];
    var z = vz[2];
    v.value = {
        x: vz[0],
        y: vz[1],
        z: vz[2]
    };
    v.isBinary = false;
}
else if (msg.value.match(/.*[^0-9\.,-].*/)) { // match if any characters are not digits, period, comma, or hyphen.
    v.value = '"' + msg.value + '"';
    v.isBinary = false;
}

// Catch any other general numerical event (carbonDioxide, power, energy, humidity, level, temperature, ultravioletIndex, voltage, etc).
else {
    v.value = Number(msg.value);
    v.isBinary = false;
}
if (msg.name == "threeAxis"){
    msg.payload = [
        {
            measurement: msg.name,
            fields: {
                valueX: v.value.x,
                valueY: v.value.y,
                valueZ: v.value.z
            },
            tags:{
                hub: msg.hubId ? msg.hubId : "HE1",
                deviceId: msg.deviceId,
                displayName: msg.displayName,
                unit: msg.unit
            },
            timestamp: new Date()
        }
    ]
} else if (v.isBinary)
{
    msg.payload = [
        {
            measurement: msg.name,
            fields: {
                value: msg.value,
                valueBinary: v.value
            },
            tags:{
                hub: msg.hubId ? msg.hubId : "HE1",
                deviceId: msg.deviceId,
                displayName: msg.displayName,
                unit: msg.unit
            },
            timestamp: new Date()
        }
    ]    
}
else
{
    msg.payload = [
        {
            measurement: msg.name,
            fields: {
                value: (Array.isArray(v.value) ? v.value.join(',') : v.value),
            },
            tags:{
                hub: msg.hubId ? msg.hubId : "HE1",
                deviceId: msg.deviceId,
                displayName: msg.displayName,
                unit: msg.unit
            },
            timestamp: new Date()
        }
    ]
}
